{"language":"python","parent":["import flet as ft

def main(page: ft.Page):
    t1 = ft.Column(
        controls=[
            ft.Text(
                value="これはテストテキストです。",
            ),
            ft.Text(
                value='value="これはテストテキストです。"',
                color=ft.colors.BLACK54,
            )
        ]
    )

    t2 = ft.Column(
        controls=[
            ft.Text(
                value="大きさや色を変えたり",
                size=50,
                color=ft.colors.PINK,
            ),
            ft.Text(
                value='value="大きさや色を変えたり"   size=50  color=ft.colors.PINK',
                color=ft.colors.BLACK54,
            )
        ]
    )

    t3 = ft.Column(
        controls=[
            ft.Text(
                value="背景色も変えられます",
                size=30,
                color=ft.colors.WHITE,
                bgcolor=ft.colors.GREEN
            ),
            ft.Text(
                value='value="背景色も変えられます"  size=30  color=ft.colors.WHITE  bgcolor=ft.colors.GREEN',
                color=ft.colors.BLACK54,
            )
        ]
    )

    t4 = ft.Column(
        controls=[
            ft.Text(
                spans=[
                    ft.TextSpan(
                        text="styleやspansを設定すると"
                    ),
                    ft.TextSpan(
                        text="斜体にしたり、",
                        style=ft.TextStyle(
                            color=ft.colors.PURPLE,
                            italic=True,
                        )
                    ),
                    ft.TextSpan(
                        text="取り消し線を引いたり、",
                        style=ft.TextStyle(
                            color=ft.colors.RED,
                            decoration=ft.TextDecoration.LINE_THROUGH,
                            decoration_thickness=3,
                        )
                    ),
                    ft.TextSpan(
                        text="影をつけたり、",
                        style=ft.TextStyle(
                            color=ft.colors.ORANGE,
                            shadow=ft.BoxShadow(
                                spread_radius=10,
                                blur_radius=3,
                                color=ft.colors.BLACK26,
                            )
                        )
                    ),
                    ft.TextSpan(
                        text="リンクを設定したり",
                        style=ft.TextStyle(
                            color=ft.colors.BLUE_500,
                            decoration=ft.TextDecoration.UNDERLINE,
                            decoration_color=ft.colors.BLUE_500,
                        ),
                        url="https://flet.dev/"
                    ),
                    ft.TextSpan(
                        text="できます。"
                    )
                ],
            ),
            ft.Text(
                value="spans=[中略・下記コードを参照]",
                color=ft.colors.BLACK54,
            )
        ]
    )

    clm = ft.Column(
        controls=[
            t1, t2, t3, t4,
        ]
    )

    page.add(clm)

ft.app(target=main)
"],"caption":[]}
