import flet as ft

def main(page: ft.Page):
    def show_button_text(e):
        print(e.control.text + " が押されました")

    button1 = ft.FilledButton(text="ボタン1", on_click=show_button_text)
    button2 = ft.FilledButton(text="ボタン2", on_click=show_button_text)
    button3 = ft.FilledButton(text="ボタン3", on_click=show_button_text)

    page.add(ft.Row(controls=[button1, button2, button3]))

ft.app(target=main)"],"caption":[]}
