{"language":"python","parent":["import flet as ft

def main(page: ft.Page):
    def change_text(e):
        tx.value = "Vertical"
        print("内容を更新しました")

    tx = ft.Text(
        value="Horizon",
        size=50
    )

    bt = ft.ElevatedButton(
        text="テキストの内容を更新",
        on_click=change_text
    )

    page.add(tx, bt)

ft.app(target=main)"],"caption":[]}
