{"language":"python","parent":["import flet as ft

def main(page: ft.Page):
    def clicked(e):
        txtfld.value = e.control.text
        txtfld.update

    button_a = ft.ElevatedButton(text="A!", on_click=clicked)
    button_b = ft.ElevatedButton(text="B!", on_click=clicked)
    button_c = ft.ElevatedButton(text="C!", on_click=clicked)
    txtfld = ft.TextField(value="")

    page.add(
        ft.Row(
            controls=[button_a, button_b, button_c]
        ),
        txtfld
    )

ft.app(target=main)"],"caption":[]}
